import React, {useState} from 'react';
import ImmutableCheckBox from './ImmutableCheckBox';
const PasswordWithRequirements = () => {
    const liStyle = {textIndent:"4px"};
    const [password, setPassword] = useState("");
    const [requirements, setRequirements] = useState({
        "length": false,
        "upperCase": false,
        "lowerCase":false,
        "oneNumber": false
    });

    const handlePasswordChange = (event) => {
        let value = event.target.value;
        setPassword(event.target.value);
        setRequirements({
            "length": value.length >= 8,
            "upperCase": /[A-Z]/.test(value),
            "lowerCase": /[a-z]/.test(value),
            "oneNumber": /\d/.test(value)
        });
    }
    return (
        <div>
            <label className="labelText" htmlFor="password">Password</label>
            <input
                id="password"
                type="password"
                value={password}
                onChange={handlePasswordChange}
            />
            <div className="indent2">
            <ImmutableCheckBox checked={requirements.length} label="at least 8" />
            </div>

        </div>

    )
}

export default PasswordWithRequirements;
