import React from 'react';
class PasswordWithRequirements extends React.component {
    constructor(props) {
        super(props);
        this.state = {
            password: '',
            requirements: ['at least 8 characters',
                'at least one uppercase letter',
                'at least one lowercase letter',
                'at least one number']
        };
    }
    handlePasswordChange = (event) => {
        const {value}= event.target.value;
        this.setState(
            {
                password: value,
                requirements: {
                    length: value.length >= 8,
                    upperCase: /[A-Z]/.test(value),
                    lowerCase: /[a-z]/.test(value),
                    digit: /\d/.test(value)
                }
            });
    }

    render() {
        const {password, requirements} = this.state;
        return (
            <div>
                    <input
                        type="password"
                        value={password}
                        onChange={this.handlePasswordChange}
                    />
                    <ul>
                        <li style={{ color: requirements.length ? 'green' : 'red' }}>
                            At least 8 characters long
                        </li>
                        <li style={{ color: requirements.uppercase ? 'green' : 'red' }}>
                            Contains an uppercase letter
                        </li>
                        <li style={{ color: requirements.lowercase ? 'green' : 'red' }}>
                            Contains a lowercase letter
                        </li>
                        <li style={{ color: requirements.number ? 'green' : 'red' }}>
                            Contains a number
                        </li>
                    </ul>
                </div>

        )
    }
}
