import React, {useState} from 'react';
const PasswordWithRequirements = () => {
    const liStyle = {textIndent:"4px"};
    const [password, setPassword] = useState("");
    const [requirements, setRequirements] = useState({
        "length": false,
        "upperCase": false,
        "lowerCase":false,
        "oneNumber": false
    });

    const handlePasswordChange = (event) => {
        let value = event.target.value;
        setPassword(event.target.value);
        setRequirements({
            "length": value.length >= 8,
            "upperCase": /[A-Z]/.test(value),
            "lowerCase": /[a-z]/.test(value),
            "oneNumber": /\d/.test(value)
        });
    }
    return (
        <div>
            <label className="labelText" htmlFor="password">password</label>
            <input
                id="password"
                type="password"
                value={password}
                onChange={handlePasswordChange}
            />
            <ul>
                <li style={liStyle}>
                    <input type="checkbox" checked={requirements.length} disabled />at least 8 characters long
                </li>
                <li style={{color: requirements.upperCase ? 'green' : 'red'}}>
                    Contains an uppercase letter
                </li>
                <li style={{color: requirements.lowerCase ? 'green' : 'red'}}>
                    Contains a lowercase letter
                </li>
                <li style={{color: requirements.oneNumber ? 'green' : 'red'}}>
                    Contains a number
                </li>
            </ul>
        </div>

    )
}

export default PasswordWithRequirements;
